import { useEffect } from "react";

import {
  ControlledConfig,
  ControlledReturn,
  FormValues,
  Methods,
} from "./types";
import * as shared from "./shared";
import {
  get,
  invariant,
  isFieldArray,
  isFieldElement,
  isUndefined,
  warn,
} from "./utils";
import useFormState from "./useFormState";

export default <V extends FormValues = FormValues>(
  name: string,
  {
    formId,
    defaultValue,
    validate,
    parse,
    format,
    errorWithTouched,
    ...props
  }: ControlledConfig<V> = {}
): ControlledReturn => {
  const methods: Methods<V> = shared.get(formId);

  invariant(
    !methods,
    'ðŸ’¡ react-cool-form > useControlled: It must work with an "useForm" hook. See: https://react-cool-form.netlify.app/docs/api-reference/use-form'
  );

  const meta = useFormState(
    {
      value: `values.${name}`,
      error: `errors.${name}`,
      isTouched: `touched.${name}`,
      isDirty: `dirty.${name}`,
    },
    { formId, errorWithTouched }
  );
  const {
    shouldRemoveField,
    initialStateRef,
    fieldArrayRef,
    controlsRef,
    fieldValidatorsRef,
    changedFieldRef,
    getState,
    getNodeValue,
    setDefaultValue,
    setTouchedMaybeValidate,
    handleChangeEvent,
    removeField,
  } = methods;

  useEffect(
    () => {
      const isFieldArr = isFieldArray(fieldArrayRef.current, name);
      const initialVal = get(initialStateRef.current.values, name);

      if (isUndefined(initialVal)) {
        if (
          !isUndefined(defaultValue) &&
          (!isFieldArr ||
            !isUndefined(
              get(initialStateRef.current.values, name.split(".")[0])
            ))
        ) {
          setDefaultValue(name, defaultValue);
        } else if (!isFieldArr) {
          warn(
            `ðŸ’¡ react-cool-form > useControlled: Please provide a default value for "${name}" field.`
          );
        }
      } else if (isUndefined(getState(name))) {
        setDefaultValue(name, initialVal);
      }

      return () => {
        if (shouldRemoveField(name))
          removeField(
            name,
            !isFieldArr ||
              isUndefined(
                // eslint-disable-next-line react-hooks/exhaustive-deps
                get(initialStateRef.current.values, name.split(".")[0])
              )
              ? undefined
              : ["defaultValue"]
          );
      };
    },
    // eslint-disable-next-line react-hooks/exhaustive-deps
    []
  );

  controlsRef.current[name] = true;
  if (validate) fieldValidatorsRef.current[name] = validate;

  const { onChange, onBlur, ...restProps } = props;
  let value = get(initialStateRef.current.values, name);
  value = !isUndefined(value) ? value : defaultValue;
  value = !isUndefined(meta.value) ? meta.value : value;
  value = (format ? format(value) : value) ?? "";

  return [
    {
      name,
      value,
      onChange: (...event) => {
        let val;

        if (parse) {
          val = parse(...event);
        } else {
          const e = event[0];
          val =
            e?.nativeEvent instanceof Event && isFieldElement(e.target)
              ? getNodeValue(name)
              : e;
        }

        handleChangeEvent(name, val);
        if (onChange) onChange(...event);
        changedFieldRef.current = name;
      },
      onBlur: (e) => {
        setTouchedMaybeValidate(name);
        if (onBlur) onBlur(e);
        changedFieldRef.current = undefined;
      },
      ...restProps,
    },
    { error: meta.error, isTouched: !!meta.isTouched, isDirty: !!meta.isDirty },
  ];
};
import { useCallback, useEffect, useState } from "react";

import {
  FieldArrayConfig,
  FieldArrayReturn,
  FormValues,
  Insert,
  Keys,
  Methods,
  Move,
  Push,
  Remove,
  StateHandler,
  Swap,
} from "./types";
import * as shared from "./shared";
import {
  compact,
  get,
  getIsDirty,
  invariant,
  isUndefined,
  set,
  setValuesAsTrue,
} from "./utils";

export default <T = any, V extends FormValues = FormValues>(
  name: string,
  { formId, defaultValue, validate }: FieldArrayConfig<T, V> = {}
): FieldArrayReturn<T> => {
  const methods: Methods<V> = shared.get(formId);

  invariant(
    !methods,
    'ðŸ’¡ react-cool-form > useFieldArray: It must work with an "useForm" hook. See: https://react-cool-form.netlify.app/docs/api-reference/use-form'
  );

  const {
    validateOnChange,
    shouldRemoveField,
    initialStateRef,
    fieldArrayRef,
    fieldValidatorsRef,
    getState,
    setDefaultValue,
    setNodesOrValues,
    setStateRef,
    runValidation,
    removeField,
  } = methods;

  const getFields = useCallback(
    (init = false): string[] => {
      let fields = getState(name);

      if (init && isUndefined(fields)) fields = defaultValue;

      return Array.isArray(fields)
        ? fields.map((_, index) => `${name}[${index}]`)
        : [];
    },
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [getState, name]
  );

  const [fields, setFields] = useState<string[]>(getFields(true));

  const updateFields = useCallback(() => {
    setFields(getFields());
    setNodesOrValues(getState("values"), {
      shouldSetValues: false,
      fields: Object.keys(fieldArrayRef.current[name].fields),
    });
  }, [fieldArrayRef, getFields, getState, name, setNodesOrValues]);

  useEffect(() => {
    if (
      isUndefined(get(initialStateRef.current.values, name)) &&
      !isUndefined(defaultValue)
    ) {
      setDefaultValue(name, defaultValue, true);
      updateFields();
    }

    return () => {
      if (shouldRemoveField(name)) removeField(name);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  if (!fieldArrayRef.current[name])
    fieldArrayRef.current[name] = {
      reset: updateFields,
      fields: {},
    };
  if (validate) fieldValidatorsRef.current[name] = validate;

  const setState = useCallback(
    (
      handler: StateHandler,
      {
        shouldTouched,
        shouldDirty,
      }: { shouldTouched?: boolean; shouldDirty?: boolean } = {}
    ) => {
      let state = getState();

      (["values", "touched", "errors", "dirty"] as Keys[]).forEach((key) => {
        const value = state[key][name];
        const fieldsLength = state.values[name]?.length;

        if (
          key === "values" ||
          (key === "touched" && shouldTouched) ||
          (key === "dirty" && shouldDirty) ||
          !isUndefined(value)
        )
          state = set(
            state,
            key,
            {
              ...state[key],
              [name]: handler(
                Array.isArray(value) ? [...value] : [],
                key,
                fieldsLength ? fieldsLength - 1 : 0
              ),
            },
            true
          );
      });

      setStateRef("", { ...state, shouldDirty: getIsDirty(state.dirty) });
      updateFields();

      if (validateOnChange) runValidation(name);
    },
    [getState, name, runValidation, setStateRef, updateFields, validateOnChange]
  );

  const push = useCallback<Push<T>>(
    (value, { shouldTouched, shouldDirty = true } = {}) => {
      const handler: StateHandler = (f, type, lastIndex = 0) => {
        if (type === "values") {
          f.push(value);
        } else if (
          (type === "touched" && shouldTouched) ||
          (type === "dirty" && shouldDirty)
        ) {
          f[lastIndex] = setValuesAsTrue(value);
        }

        return f;
      };

      setState(handler, { shouldTouched, shouldDirty });
    },
    [setState]
  );

  const insert = useCallback<Insert<T>>(
    (index, value, { shouldTouched, shouldDirty = true } = {}) => {
      const handler: StateHandler = (f, type) => {
        if (type === "values") {
          f.splice(index, 0, value);
        } else if (
          (type === "touched" && shouldTouched) ||
          (type === "dirty" && shouldDirty)
        ) {
          f[index] = setValuesAsTrue(value);
        } else if (index < f.length) {
          f.splice(index, 0, undefined);
        }

        return f;
      };

      setState(handler, { shouldTouched, shouldDirty });
    },
    [setState]
  );

  const remove = useCallback<Remove<T>>(
    (index) => {
      const handler: StateHandler = (f) => {
        f.splice(index, 1);
        return compact(f).length ? f : [];
      };
      const value = (getState(name) || [])[index];

      setState(handler);

      return value;
    },
    [getState, name, setState]
  );

  const swap = useCallback<Swap>(
    (indexA, indexB) => {
      const handler: StateHandler = (f) => {
        [f[indexA], f[indexB]] = [f[indexB], f[indexA]];
        return f;
      };

      setState(handler);
    },
    [setState]
  );

  const move = useCallback<Move>(
    (from, to) => {
      const handler: StateHandler = (f) => {
        f.splice(to, 0, f.splice(from, 1)[0]);
        return f;
      };

      setState(handler);
    },
    [setState]
  );

  return [fields, { push, insert, remove, swap, move }];
};
