import {useEffect,useState} from 'react';
import { BrowserRouter as Router, Route, Link, useNavigate } from 'react-router-dom';
import Question from './components/Question_display';
import React from 'react'
import './App.css';

function App() {
  const [userData, setUserData] = useState([]);
  useEffect(() => {
    fetch('http://localhost:5000/user')
    .then((res)=>res.json())
    .then((json)=>{
      setUserData(json);
    })
  },[]);
  const [formData, setFormData] = useState({
    total_marks: "",
    easy: "",
    medium: "",
    hard: "",

  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };
  const navigate = useNavigate();
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/submitform', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      console.log(data);

      // Handle the server response as needed
      setFormData({
        total_marks: '',
        easy: '',
        medium: '',
        hard: '',
      });

      // Navigate only after the data is successfully posted
      navigate('/question_generator');

      // Log after successful navigation
      console.log('Form submitted with data:', formData);
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (

    <div className="App">
       <h1>Hello</h1>
      {/* <h1>
        {userData?(
          <h1>{userData[0].message}</h1>
        ):(
          <h1>Welcome to the Quiz App</h1>
        )}
      </h1> */}

      <form onSubmit={handleSubmit}>
      <label>
        Total Marks:
        <input
          type="total_marks"
          name="total_marks"
          value={formData.total_marks}
          onChange={handleInputChange}
          required
        />
      </label>
      <br />
      <label>
        EASY:
        <input
          type="easy"
          name="easy"
          value={formData.easy}
          onChange={handleInputChange}
          required
        />
      </label>
      <label>
        medium:
        <input
          type="medium"
          name="medium"
          value={formData.medium}
          onChange={handleInputChange}
          required
        />
      </label>
      <label>
        hard:
        <input
          type="hard"
          name="hard"
          value={formData.hard}
          onChange={handleInputChange}
          required
        />
      </label>
      <br />
      {/* <Link to="/question_generated"> */}
      <button type="submit">Generate Questions</button>
      {/* </Link> */}
    </form>

    </div>

  );
}

export default App;
