import {useEffect,useState} from 'react';
import { BrowserRouter as Router, Route, Link, useNavigate } from 'react-router-dom';
import Question from './components/Question_display';
import React from 'react'
import './App.css';

function App() {
  const [userData, setUserData] = useState([]);
  useEffect(() => {
    fetch('http://localhost:5000/user')
    .then((res)=>res.json())
    .then((json)=>{
      setUserData(json);
    })
  },[]);
  const [formData, setFormData] = useState({
    total_marks: "",
    easy: "",
    medium: "",
    hard: "",

  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };
  const navigate = useNavigate();
  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/submitform', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json', // Set Content-Type header to application/json
            },
            body: JSON.stringify(formData),
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                // Handle the server response as needed
                setFormData({
                  total_marks: "",
                  easy: "",
                  medium: "",
                  hard: "",
                });
            })

            .catch(error => {
                console.error('Error submitting form:', error);
            });

    // Add your logic to handle form submission, such as sending data to the server
    navigate('/question_generator');
    console.log('Form submitted with data:', formData);
  };

  return (
    <Router>
    <div className="App">
       <h1>Hello</h1>
      <h1>{userData.message}</h1>

      <form onSubmit={handleSubmit}>
      <label>
        Total Marks:
        <input
          type="total_marks"
          name="total_marks"
          value={formData.total_marks}
          onChange={handleInputChange}
          required
        />
      </label>
      <br />
      <label>
        EASY:
        <input
          type="easy"
          name="easy"
          value={formData.easy}
          onChange={handleInputChange}
          required
        />
      </label>
      <label>
        medium:
        <input
          type="medium"
          name="medium"
          value={formData.medium}
          onChange={handleInputChange}
          required
        />
      </label>
      <label>
        hard:
        <input
          type="hard"
          name="hard"
          value={formData.hard}
          onChange={handleInputChange}
          required
        />
      </label>
      <br />
      <button type="submit">Generate Questions</button>
    </form>
    </div>
    <Route path="/question_generator" component={Question} />

    </Router>
  );
}

export default App;
